from middlerware.handler import Handler,replace_datafrom common.requset_handler import visitimport ddtimport unittestimport json# 获取车辆信息car_info_data = Handler.excel.read_sheet("车辆信息")logger = Handler.logger@ddt.ddtclass TestCarInfo(unittest.TestCase):    @ddt.data(*car_info_data)    def test_car_info(self, test_info):        test_info["data"] = replace_data(Handler(), test_info["data"])        test_info["expected"] = replace_data(Handler(), test_info["expected"])        resp = visit(method=test_info["method"],                     url=Handler.host + test_info["url"],                     json=json.loads(test_info["data"]))        expected = json.loads(test_info["expected"])        self.assertTrue(expected["code"] == resp["code"])        self.assertTrue(expected["msg"] == resp["msg"])        if test_info["case_id"] == 1:            self.assertTrue(expected["data"]["carId"] == resp["data"]["carId"])        logger.info("第{}条测试用例通过".format(test_info["case_id"]))if __name__ == '__main__':    unittest.main()