from middlerware.handler import Handlerfrom common.requset_handler import visitimport ddtimport unittestimport json# 获取车辆传感器位置数据getSensor_data = Handler.excel.read_sheet("获取车辆传感器位置")logger = Handler.logger@ddt.ddtclass TestGetSensor(unittest.TestCase):    @ddt.data(*getSensor_data)    def test_get_sensor(self, test_info):        resp = visit(method=test_info["method"],                     url=Handler.host + test_info["url"],                     json=json.loads(test_info["data"]))        expected = json.loads(test_info["expected"])        self.assertTrue(expected["code"] == resp["code"])        self.assertTrue(expected["msg"] == resp["msg"])        if test_info["case_id"] == 1:            self.assertTrue(expected["data"][0]["sensorId"] == resp["data"][0]["sensorId"])            self.assertTrue(expected["data"][0]["idx"] == resp["data"][0]["idx"])        logger.info("第{}条测试用例通过".format(test_info["case_id"]))if __name__ == '__main__':    unittest.main()