from middlerware.handler import Handlerfrom common.requset_handler import visitimport ddtimport unittestimport json# 获取胎压pressure_data = Handler.excel.read_sheet("获取指定胎压数据")logger = Handler.logger@ddt.ddtclass TestGetPresure(unittest.TestCase):    @ddt.data(*pressure_data)    def test_get_pressure(self, test_info):        resp = visit(method=test_info["method"],                     url=Handler.host + test_info["url"],                     json=json.loads(test_info["data"]))        expected = json.loads(test_info["expected"])        self.assertTrue(expected["code"] == resp["code"])        self.assertTrue(expected["msg"] == resp["msg"])        self.assertTrue(expected["data"]["carId"] == resp["data"]["carId"])        logger.info("第{}条测试用例通过".format(test_info["case_id"]))if __name__ == '__main__':    unittest.main()