from middlerware.handler import Handlerfrom common.requset_handler import visitimport ddtimport unittestimport json# 热门搜索数据hot_brand_data = Handler.excel.read_sheet("热门搜索")# 初始化loggerlogger = Handler.logger@ddt.ddtclass TestHotBrand(unittest.TestCase):    @ddt.data(*hot_brand_data)    def test_hot_brand(self, test_info):        resp = visit(method=test_info["method"],                     url=Handler.host + test_info["url"],                     json=json.loads(test_info["data"]))        expected = json.loads(test_info["expected"])        self.assertTrue(expected["code"] == resp["code"])        self.assertTrue(expected["msg"] == resp["msg"])        # self.assertTrue(expected["data"][0]["series"] == resp["data"][0]["series"])        logger.info("第{}条测试用例通过".format(test_info["case_id"]))if __name__ == '__main__':    unittest.main()