from middlerware.handler import Handlerfrom common.requset_handler import visitimport ddtimport unittestimport json# 获取用户信息modify_user_info_data = Handler.excel.read_sheet("修改用户信息")# 初始化loggerlogger = Handler.logger@ddt.ddtclass TestUserInfo(unittest.TestCase):    @ddt.data(*modify_user_info_data)    def test_modify_user_info(self, test_info):        resp = visit(method=test_info["method"],                     url=Handler.host + test_info["url"],                     json=json.loads(test_info["data"]))        expected = json.loads(test_info["expected"])        self.assertTrue(expected["code"] == resp["code"])        self.assertTrue(expected["msg"] == resp["msg"])        if test_info["case_id"] == 2:            for k, v in expected["data"].items():                self.assertEqual(resp["data"][k], v)        logger.info("第{}条测试用例通过".format(test_info["case_id"]))if __name__ == '__main__':    unittest.main()