from middlerware.handler import Handler, replace_datafrom common.requset_handler import visitimport ddtimport unittestimport json# 提交报警设置subAlarm_data = Handler.excel.read_sheet("提交报警设置")# 初始化loggerlogger = Handler.logger@ddt.ddtclass TestSubAlarm(unittest.TestCase):    @ddt.data(*subAlarm_data)    def test_subAlarm(self, test_info):        test_info["data"] = replace_data(Handler(), test_info["data"])        test_info["expected"] = replace_data(Handler(), test_info["expected"])        resp = visit(method=test_info["method"],                     url=Handler.host + test_info["url"],                     json=json.loads(test_info["data"]))        expected = json.loads(test_info["expected"])        for k, v in expected.items():            self.assertEqual(resp[k], v)        logger.info("第{}条测试用例通过".format(test_info["case_id"]))if __name__ == '__main__':    unittest.main()