from middlerware.handler import Handler, replace_datafrom common.requset_handler import visitimport ddtimport unittestimport json# 提交传感器位置匹配subSensor_data = Handler.excel.read_sheet("提交传感器位置匹配")# for i in subSensor_data:#     print(i)# 初始化loggerlogger = Handler.logger@ddt.ddtclass TestSubSensor(unittest.TestCase):    @ddt.data(*subSensor_data)    def test_subSensor(self, test_info):        test_info["data"] = replace_data(Handler(), test_info["data"])        resp = visit(method=test_info["method"],                     url=Handler.host + test_info["url"],                     json=json.loads(test_info["data"]))        expected = json.loads(test_info["expected"])        for k, v in expected.items():            self.assertEqual(resp[k], v)        logger.info("第{}条测试用例通过".format(test_info["case_id"]))if __name__ == '__main__':    unittest.main()