from middlerware.handler import Handlerfrom common.requset_handler import visitimport ddtimport unittestimport json# 上传头像upload_data = Handler.excel.read_sheet("头像上传")# 初始化loggerlogger = Handler.logger@ddt.ddtclass TestUpload(unittest.TestCase):    @ddt.data(*upload_data)    def test_upload(self, test_info):        resp = visit(method=test_info["method"],                     url=Handler.host + test_info["url"],                     files=eval(test_info["data"]))        expected = json.loads(test_info["expected"])        self.assertTrue(expected["code"] == resp["code"])        logger.info("第{}条测试用例通过".format(test_info["case_id"]))if __name__ == '__main__':    unittest.main()