from middlerware.handler import Handler, replace_datafrom common.requset_handler import visitimport ddtimport unittestimport json# 从Excel表格中获取验证设备用例数据verify_device_data = Handler.excel.read_sheet("设备验证")# 初始化loggerlogger = Handler.logger@ddt.ddtclass TestVerifyDevice(unittest.TestCase):    @ddt.data(*verify_device_data)    def test_verify_device(self, test_info):        test_info["data"] = replace_data(Handler(), test_info["data"])        test_info["expected"] = replace_data(Handler(), test_info["expected"])        resp = visit(method=test_info["method"],                     url=Handler.host + test_info["url"],                     json=json.loads(test_info["data"]))        expected = json.loads(test_info["expected"])        self.assertTrue(expected["code"] == resp["code"])        self.assertTrue(expected["msg"] == resp["msg"])        if test_info["case_id"] == 2:            self.assertTrue(expected["data"]["sn"] == resp["data"]["sn"])        logger.info("第{}条测试用例通过".format(test_info["case_id"]))if __name__ == '__main__':    unittest.main()